package com.practice.test;

public class Test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=0;
		int b=0;
		int c=0;
		int d=0;
		System.out.println(a++);
		System.out.println(a);
		System.out.println(++b);
		System.out.println(c--);
		System.out.println(--d);
	}

}
